const nameElement=document.getElementById("nav-name"),parentElement=document.getElementById("nav-main");let incremented=0;function shiftNavIcons(){let e=document.getElementById("nav-heading");nameElement.offsetWidth<e.offsetWidth+10?parentElement.classList.add("nav-shifted"):(incremented++,incremented>5&&parentElement.offsetWidth>445&&(parentElement.className=parentElement.className.replace(/\bnav-shifted\b/g,""),incremented=0))}window.addEventListener("resize",e=>{shiftNavIcons()}),shiftNavIcons()